import React, { useState } from 'react';
import styled, { createGlobalStyle, ThemeProvider } from 'styled-components';
import { FaMapMarkerAlt, FaClock, FaGraduationCap, FaCalendarAlt, FaSun, FaMoon } from 'react-icons/fa';
import { MapContainer, TileLayer, Marker, Popup, useMap } from 'react-leaflet';
import 'leaflet/dist/leaflet.css';
import L from 'leaflet';
import { Carousel } from 'react-responsive-carousel';
import 'react-responsive-carousel/lib/styles/carousel.min.css';

// Fix for leaflet marker icons
delete L.Icon.Default.prototype._getIconUrl;
L.Icon.Default.mergeOptions({
  iconRetinaUrl: 'https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.7.1/images/marker-icon-2x.png',
  iconUrl: 'https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.7.1/images/marker-icon.png',
  shadowUrl: 'https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.7.1/images/marker-shadow.png',
});

// Image imports (in a real app, these would be actual imports)
const images = {
  logo: 'logo.png',
  doctorImage: 'doctor.jpg',
  doctorProfile: 'medical.png',
  mapImage: 'map.jpg',
  calendarImage: 'may-calendar.png',
  stethoscopeImage: 'stethoscope.png',
  patientImage: 'patient.png',
  backPain: 'back-pain.jpg',
  wristPain: 'wrist-joint-pain.jpg',
  slippedDisc: 'slipped-disc.jpg',
  neckPain: 'neck-pain.jpg',
  anklePain: 'ankle-joint-pain.png',
  headache: 'headache.jpg',
  kneePain: 'knee-pain.jpg',
  sportInjuries: 'sport-injuries.jpg',
};

// Theme definitions
const lightTheme = {
  colors: {
    primary: '#059669',
    secondary: '#3B82F6',
    background: '#ffffff',
    text: '#333333',
    textLight: '#666666',
    headerBg: '#ffffff',
    cardBg: '#ffffff',
    footerBg: '#0f172a',
    footerText: '#cbd5e1',
  },
  shadows: {
    small: '0 2px 15px rgba(0, 0, 0, 0.1)',
    medium: '0 5px 15px rgba(0, 0, 0, 0.1)',
    large: '0 10px 25px rgba(0, 0, 0, 0.1)',
  },
};

const darkTheme = {
  colors: {
    primary: '#10b981',
    secondary: '#60a5fa',
    background: '#1e293b',
    text: '#f8fafc',
    textLight: '#94a3b8',
    headerBg: '#1e293b',
    cardBg: '#334155',
    footerBg: '#0f172a',
    footerText: '#cbd5e1',
  },
  shadows: {
    small: '0 2px 15px rgba(0, 0, 0, 0.3)',
    medium: '0 5px 15px rgba(0, 0, 0, 0.3)',
    large: '0 10px 25px rgba(0, 0, 0, 0.3)',
  },
};

// Global styles
const GlobalStyles = createGlobalStyle`
  * {
    margin: 0;
    padding: 0;
    box-sizing: border-box;
    transition: background-color 0.3s, color 0.3s;
  }

  body {
    font-family: 'Poppins', sans-serif;
    line-height: 1.6;
    background-color: ${({ theme }) => theme.colors.background};
    color: ${({ theme }) => theme.colors.text};
  }

  a {
    text-decoration: none;
    color: inherit;
  }

  button {
    cursor: pointer;
  }

  .leaflet-container {
    height: 300px;
    width: 100%;
    border-radius: 8px;
  }
`;

const App = () => {
  const [darkMode, setDarkMode] = useState(false);
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    phone: '',
    message: '',
    date: '',
    time: '',
    reason: '',
  });
  const [formErrors, setFormErrors] = useState({});
  const [isSubmitted, setIsSubmitted] = useState(false);
  const [activeTab, setActiveTab] = useState('contact');

  // Clinic location coordinates
  const clinicLocation = [19.0760, 72.8777]; // Mumbai coordinates

  // Services data
  const servicesData = [
    { id: 1, img: images.backPain, title: 'Back Pain Treatment' },
    { id: 2, img: images.wristPain, title: 'Joint Pain Treatment' },
    { id: 3, img: images.slippedDisc, title: 'Slipped Disc Treatment' },
    { id: 4, img: images.neckPain, title: 'Neck Pain Treatment' },
    { id: 5, img: images.anklePain, title: 'Joint Pain Treatment' },
    { id: 6, img: images.headache, title: 'Headache Treatment' },
    { id: 7, img: images.kneePain, title: 'Knee Pain Treatment' },
    { id: 8, img: images.sportInjuries, title: 'Sports Injuries Treatment' },
  ];

  // Patient resources data
  const resourcesData = [
    { 
      id: 1, 
      type: 'testimonial',
      img: images.patientImage,
      content: '"Dr. Rajeev Menon is truly a great doctor. He has lots of experience and his approach is very friendly."',
      author: '- Priya Mehta',
      buttonText: 'Read More'
    },
    { 
      id: 2, 
      type: 'faq',
      title: 'Frequently Asked Questions',
      items: [
        'How can I get my prescription?',
        'What should I bring to my appointment?',
        'What should I expect during my first checkup?'
      ],
      buttonText: 'View all'
    },
    { 
      id: 3, 
      type: 'calendar',
      title: 'Schedule an Appointment',
      img: images.calendarImage,
      icon: <FaCalendarAlt size={24} />
    },
    { 
      id: 4, 
      type: 'blog',
      title: 'Blog',
      img: images.stethoscopeImage,
      content: 'Understanding Chronic Pain',
      buttonText: 'Read more'
    }
  ];

  // Testimonials for carousel
  const testimonials = [
    {
      id: 1,
      name: 'Priya Mehta',
      role: 'Patient',
      content: 'Dr. Rajeev Menon is truly a great doctor. He has lots of experience and his approach is very friendly.',
      rating: 5
    },
    {
      id: 2,
      name: 'Rahul Sharma',
      role: 'Athlete',
      content: 'After my sports injury, Dr. Menon helped me recover faster than expected. Highly recommended!',
      rating: 5
    },
    {
      id: 3,
      name: 'Ananya Patel',
      role: 'Office Worker',
      content: 'My chronic back pain is finally manageable thanks to the treatment plan from RelivaWell.',
      rating: 4
    }
  ];

  // Handle form input changes
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  // Form validation
  const validateForm = () => {
    const errors = {};
    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    const phoneRegex = /^[0-9]{10}$/;

    if (!formData.name.trim()) errors.name = 'Name is required';
    if (!formData.email) {
      errors.email = 'Email is required';
    } else if (!emailRegex.test(formData.email)) {
      errors.email = 'Please enter a valid email';
    }
    if (!formData.phone) {
      errors.phone = 'Phone is required';
    } else if (!phoneRegex.test(formData.phone)) {
      errors.phone = 'Please enter a valid 10-digit phone number';
    }
    if (activeTab === 'contact' && !formData.message.trim()) {
      errors.message = 'Message is required';
    }
    if (activeTab === 'appointment') {
      if (!formData.date) errors.date = 'Date is required';
      if (!formData.time) errors.time = 'Time is required';
      if (!formData.reason.trim()) errors.reason = 'Reason is required';
    }

    return errors;
  };

  // Handle form submission
  const handleSubmit = (e) => {
    e.preventDefault();
    const errors = validateForm();

    if (Object.keys(errors).length === 0) {
      // In a real app, you would send this data to your backend
      console.log('Form submitted:', formData);
      setIsSubmitted(true);
      setFormErrors({});

      // Reset form after submission
      setTimeout(() => {
        setIsSubmitted(false);
        setFormData({
          name: '',
          email: '',
          phone: '',
          message: '',
          date: '',
          time: '',
          reason: '',
        });
      }, 3000);
    } else {
      setFormErrors(errors);
    }
  };

  // Custom map component with zoom control
  const MapWithZoom = () => {
    const map = useMap();

    const handleZoomChange = (e) => {
      map.setZoom(e.target.value);
    };

    return (
      <MapZoomControl>
        <label>Zoom Level</label>
        <RangeInput 
          type="range" 
          min="1" 
          max="20" 
          defaultValue="10" 
          onChange={handleZoomChange}
        />
      </MapZoomControl>
    );
  };

  return (
    <ThemeProvider theme={darkMode ? darkTheme : lightTheme}>
      <GlobalStyles />
      <AppContainer>
        <Header>
          <LogoContainer>
            <LogoImg src={images.logo} alt="RelivaWell logo"/>
            <LogoText>RelivaWell</LogoText>
          </LogoContainer>
          <Nav>
            <NavLink href="#home">Home</NavLink>
            <NavLink href="#about">About</NavLink>
            <NavLink href="#services">Services</NavLink>
            <NavLink href="#resources">Resources</NavLink>
            <NavLink href="#contact">Contact</NavLink>
          </Nav>
          <HeaderControls>
            <ThemeToggle onClick={() => setDarkMode(!darkMode)}>
              {darkMode ? <FaSun size={20} /> : <FaMoon size={20} />}
            </ThemeToggle>
            <AuthButtons>
              <LoginButton>Log In</LoginButton>
              <SignupButton>Sign Up</SignupButton>
            </AuthButtons>
          </HeaderControls>
        </Header>

        <Main>
          {/* Hero Section */}
          <HeroSection id="home">
            <HeroContent>
              <HeroTitle>
                <span>Stop</span><strong> Guessing.</strong><br />
                <BlueText>Start </BlueText><strong>Healing.</strong>
              </HeroTitle>
              <HeroDescription>
                Personalized, compassionate care for chronic pain and fibromyalgia, and unexplained symptoms.
              </HeroDescription>
              <PrimaryButton>Start Your Healing Journey</PrimaryButton>
            </HeroContent>
            <HeroImage src={images.doctorImage} alt="Doctor consulting patient"/>
          </HeroSection>

          {/* Doctor Profile Section */}
          <DoctorSection id="about">
            <DoctorContainer>
              <DoctorImage src={images.doctorProfile} alt="Dr. Rajeev Menon"/>
              <DoctorDetails>
                <DoctorName>Dr. Rajeev Menon</DoctorName>
                <DoctorSpecialty>Orthopaedic Surgery & Joint Pain</DoctorSpecialty>
                <DoctorQualification>
                  <FaGraduationCap /> MBBS MS(Orthopedics)
                </DoctorQualification>
                <DoctorExperience>
                  <FaClock /> 13 years experience
                </DoctorExperience>
                <DoctorBio>
                  He treats whole body pain by finding and healing the root cause. With years of experience in pain medicine, he helps patients get relief.
                </DoctorBio>
                <PrimaryButton>Get In Touch</PrimaryButton>
              </DoctorDetails>
            </DoctorContainer>
          </DoctorSection>

          {/* Testimonials Carousel */}
          <TestimonialsSection>
            <SectionHeader>
              <SectionTitle>Patient <BlueText>Testimonials</BlueText></SectionTitle>
            </SectionHeader>
            <StyledCarousel
              showArrows={true}
              infiniteLoop={true}
              showThumbs={false}
              showStatus={false}
              autoPlay={true}
              interval={5000}
            >
              {testimonials.map(testimonial => (
                <TestimonialCard key={testimonial.id}>
                  <TestimonialContent>
                    <TestimonialText>"{testimonial.content}"</TestimonialText>
                    <TestimonialAuthor>
                      <strong>{testimonial.name}</strong> - {testimonial.role}
                    </TestimonialAuthor>
                    <Rating>
                      {[...Array(5)].map((_, i) => (
                        <Star key={i} filled={i < testimonial.rating} />
                      ))}
                    </Rating>
                  </TestimonialContent>
                </TestimonialCard>
              ))}
            </StyledCarousel>
          </TestimonialsSection>

          {/* Services Section */}
          <ServicesSection id="services">
            <SectionHeader>
              <SectionSubtitle>Our Services</SectionSubtitle>
              <SectionTitle>Pain <BlueText>Conditions</BlueText> Treated</SectionTitle>
            </SectionHeader>
            <ServicesGrid>
              {servicesData.map(service => (
                <ServiceCard key={service.id}>
                  <ServiceImage src={service.img} alt={service.title}/>
                  <ServiceTitle>{service.title}</ServiceTitle>
                </ServiceCard>
              ))}
            </ServicesGrid>
            <SeeMoreButton>See more →</SeeMoreButton>
          </ServicesSection>

          {/* Patient Resources Section */}
          <PatientResourcesSection id="resources">
            <SectionHeader>
              <SectionTitle>Patient <BlueText>Resources</BlueText></SectionTitle>
              <SectionDescription>
                Access helpful information and patient testimonials or schedule an appointment.
              </SectionDescription>
            </SectionHeader>

            <ResourcesGrid>
              {resourcesData.map(resource => (
                <ResourceCard key={resource.id} type={resource.type}>
                  {resource.type === 'testimonial' && (
                    <>
                      <PatientImage src={resource.img} alt="Patient"/>
                      <TestimonialText>{resource.content}</TestimonialText>
                      <PatientName>{resource.author}</PatientName>
                      <PrimaryButton>{resource.buttonText}</PrimaryButton>
                    </>
                  )}

                  {resource.type === 'faq' && (
                    <>
                      <ResourceTitle>{resource.title}</ResourceTitle>
                      <FaqList>
                        {resource.items.map((item, index) => (
                          <FaqItem key={index}>{item}</FaqItem>
                        ))}
                      </FaqList>
                      <PrimaryButton>{resource.buttonText}</PrimaryButton>
                    </>
                  )}

                  {resource.type === 'calendar' && (
                    <>
                      <ResourceTitleWithIcon>
                        {resource.icon}
                        {resource.title}
                      </ResourceTitleWithIcon>
                      <CalendarImage src={resource.img} alt="Calendar"/>
                      <PrimaryButton>Book Now</PrimaryButton>
                    </>
                  )}

                  {resource.type === 'blog' && (
                    <>
                      <ResourceTitle>{resource.title}</ResourceTitle>
                      <BlogImage src={resource.img} alt="Blog"/>
                      <BlogExcerpt>{resource.content}</BlogExcerpt>
                      <PrimaryButton>{resource.buttonText}</PrimaryButton>
                    </>
                  )}
                </ResourceCard>
              ))}
            </ResourcesGrid>

            {/* Contact and Appointment Section */}
            <ContactSection id="contact">
              <FormTabs>
                <TabButton 
                  active={activeTab === 'contact'} 
                  onClick={() => setActiveTab('contact')}
                >
                  Contact Us
                </TabButton>
                <TabButton 
                  active={activeTab === 'appointment'} 
                  onClick={() => setActiveTab('appointment')}
                >
                  Book Appointment
                </TabButton>
              </FormTabs>

              <FormContainer>
                {isSubmitted ? (
                  <SuccessMessage>
                    <h3>Thank you for your submission!</h3>
                    <p>We'll get back to you soon.</p>
                  </SuccessMessage>
                ) : (
                  <Form onSubmit={handleSubmit}>
                    <FormGroup>
                      <FormLabel>Name</FormLabel>
                      <FormInput 
                        type="text" 
                        name="name" 
                        value={formData.name}
                        onChange={handleInputChange}
                        placeholder="Your Name"
                        error={formErrors.name}
                      />
                      {formErrors.name && <ErrorText>{formErrors.name}</ErrorText>}
                    </FormGroup>

                    <FormGroup>
                      <FormLabel>Email</FormLabel>
                      <FormInput 
                        type="email" 
                        name="email" 
                        value={formData.email}
                        onChange={handleInputChange}
                        placeholder="Your Email"
                        error={formErrors.email}
                      />
                      {formErrors.email && <ErrorText>{formErrors.email}</ErrorText>}
                    </FormGroup>

                    <FormGroup>
                      <FormLabel>Phone</FormLabel>
                      <FormInput 
                        type="tel" 
                        name="phone" 
                        value={formData.phone}
                        onChange={handleInputChange}
                        placeholder="Your Phone Number"
                        error={formErrors.phone}
                      />
                      {formErrors.phone && <ErrorText>{formErrors.phone}</ErrorText>}
                    </FormGroup>

                    {activeTab === 'contact' ? (
                      <FormGroup>
                        <FormLabel>Message</FormLabel>
                        <FormTextarea 
                          name="message" 
                          value={formData.message}
                          onChange={handleInputChange}
                          placeholder="Your Message"
                          error={formErrors.message}
                        />
                        {formErrors.message && <ErrorText>{formErrors.message}</ErrorText>}
                      </FormGroup>
                    ) : (
                      <>
                        <FormGroup>
                          <FormLabel>Date</FormLabel>
                          <FormInput 
                            type="date" 
                            name="date" 
                            value={formData.date}
                            onChange={handleInputChange}
                            error={formErrors.date}
                          />
                          {formErrors.date && <ErrorText>{formErrors.date}</ErrorText>}
                        </FormGroup>

                        <FormGroup>
                          <FormLabel>Time</FormLabel>
                          <FormInput 
                            type="time" 
                            name="time" 
                            value={formData.time}
                            onChange={handleInputChange}
                            error={formErrors.time}
                          />
                          {formErrors.time && <ErrorText>{formErrors.time}</ErrorText>}
                        </FormGroup>

                        <FormGroup>
                          <FormLabel>Reason for Visit</FormLabel>
                          <FormInput 
                            type="text" 
                            name="reason" 
                            value={formData.reason}
                            onChange={handleInputChange}
                            placeholder="Briefly describe your reason for visit"
                            error={formErrors.reason}
                          />
                          {formErrors.reason && <ErrorText>{formErrors.reason}</ErrorText>}
                        </FormGroup>
                      </>
                    )}

                    <PrimaryButton type="submit">
                      {activeTab === 'contact' ? 'Send Message' : 'Book Appointment'}
                    </PrimaryButton>
                  </Form>
                )}
              </FormContainer>

              <MapWrapper>
                <SectionTitle>
                  <FaMapMarkerAlt /> Clinic Location
                </SectionTitle>
                <MapContainer center={clinicLocation} zoom={13} scrollWheelZoom={false}>
                  <TileLayer
                    url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
                    attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
                  />
                  <Marker position={clinicLocation}>
                    <Popup>
                      RelivaWell Clinic <br /> 123 Healing Street, Mumbai
                    </Popup>
                  </Marker>
                  <MapWithZoom />
                </MapContainer>
                <MapAddress>
                  <strong>Address:</strong> 123 Healing Street, Health District, Mumbai 400001
                </MapAddress>
                <MapHours>
                  <strong>Hours:</strong> Mon-Sat: 9:00 AM - 7:00 PM | Sun: Emergency Only
                </MapHours>
              </MapWrapper>
            </ContactSection>
          </PatientResourcesSection>
        </Main>

        {/* Footer */}
        <Footer>
          <FooterColumn>
            <FooterTitle>Contact With Us</FooterTitle>
            <FooterText>info@relivawell.com</FooterText>
            <FooterText>+91 98765 43210</FooterText>
          </FooterColumn>

          <FooterColumn>
            <FooterTitle>Quick Links</FooterTitle>
            <FooterLink href="#home">Home</FooterLink>
            <FooterLink href="#about">About</FooterLink>
            <FooterLink href="#services">Services</FooterLink>
            <FooterLink href="#resources">Resources</FooterLink>
            <FooterLink href="#contact">Contact</FooterLink>
          </FooterColumn>

          <FooterColumn>
            <FooterTitle><FaClock /> Clinic Hours</FooterTitle>
            <FooterText>Mon-Sat: 9:00 AM - 7:00 PM</FooterText>
            <FooterText>Sun: Emergency Only</FooterText>
          </FooterColumn>

          <FooterColumn>
            <FooterTitle>Newsletter</FooterTitle>
            <NewsletterForm>
              <NewsletterInput type="email" placeholder="Your Email" />
              <NewsletterButton>Subscribe</NewsletterButton>
            </NewsletterForm>
            <SocialIcons>
              <SocialIcon href="#"><i className="fab fa-facebook-f"></i></SocialIcon>
              <SocialIcon href="#"><i className="fab fa-twitter"></i></SocialIcon>
              <SocialIcon href="#"><i className="fab fa-instagram"></i></SocialIcon>
              <SocialIcon href="#"><i className="fab fa-linkedin-in"></i></SocialIcon>
            </SocialIcons>
          </FooterColumn>
        </Footer>

        <Copyright>
          &copy; {new Date().getFullYear()} RelivaWell. All rights reserved.
        </Copyright>
      </AppContainer>
    </ThemeProvider>
  );
};

// Star rating component
const Star = ({ filled }) => (
  <span style={{ color: filled ? '#f59e0b' : '#cbd5e1' }}>★</span>
);

// Styled components
