import React, { useState } from 'react';
import styled, { createGlobalStyle, ThemeProvider } from 'styled-components';
import { FaMapMarkerAlt, FaClock, FaGraduationCap, FaCalendarAlt, FaSun, FaMoon } from 'react-icons/fa';
import { MapContainer, TileLayer, Marker, Popup, useMap } from 'react-leaflet';
import 'leaflet/dist/leaflet.css';
import L from 'leaflet';
import { Carousel } from 'react-responsive-carousel';
import 'react-responsive-carousel/lib/styles/carousel.min.css';

// Import all images
import logo from './assets/logo.png';
import doctorImage from './assets/doctor.jpg';
import doctorProfile from './assets/medical.png';
import mapImage from './assets/map.jpg';
import calendarImage from './assets/may-calendar.png';
import stethoscopeImage from './assets/stethoscope.png';
import patientImage from './assets/patient.png';
import backPain from './assets/back-pain.jpg';
import wristPain from './assets/wrist-joint-pain.jpg';
import slippedDisc from './assets/slipped-disc.jpg';
import neckPain from './assets/neck-pain.jpg';
import anklePain from './assets/ankle-joint-pain.png';
import headache from './assets/headache.jpg';
import kneePain from './assets/knee-pain.jpg';
import sportInjuries from './assets/sport-injuries.jpg';

// Fix for leaflet marker icons
delete L.Icon.Default.prototype._getIconUrl;
L.Icon.Default.mergeOptions({
  iconRetinaUrl: 'https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.7.1/images/marker-icon-2x.png',
  iconUrl: 'https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.7.1/images/marker-icon.png',
  shadowUrl: 'https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.7.1/images/marker-shadow.png',
});

// Image object
const images = {
  logo,
  doctorImage,
  doctorProfile,
  mapImage,
  calendarImage,
  stethoscopeImage,
  patientImage,
  backPain,
  wristPain,
  slippedDisc,
  neckPain,
  anklePain,
  headache,
  kneePain,
  sportInjuries,
};

// Theme definitions
const lightTheme = {
  colors: {
    primary: '#059669',
    secondary: '#3B82F6',
    background: '#ffffff',
    text: '#333333',
    textLight: '#666666',
    headerBg: '#ffffff',
    cardBg: '#ffffff',
    footerBg: '#0f172a',
    footerText: '#cbd5e1',
  },
  shadows: {
    small: '0 2px 15px rgba(0, 0, 0, 0.1)',
    medium: '0 5px 15px rgba(0, 0, 0, 0.1)',
    large: '0 10px 25px rgba(0, 0, 0, 0.1)',
  },
};

const darkTheme = {
  colors: {
    primary: '#10b981',
    secondary: '#60a5fa',
    background: '#1e293b',
    text: '#f8fafc',
    textLight: '#94a3b8',
    headerBg: '#1e293b',
    cardBg: '#334155',
    footerBg: '#0f172a',
    footerText: '#cbd5e1',
  },
  shadows: {
    small: '0 2px 15px rgba(0, 0, 0, 0.3)',
    medium: '0 5px 15px rgba(0, 0, 0, 0.3)',
    large: '0 10px 25px rgba(0, 0, 0, 0.3)',
  },
};

// Global styles
const GlobalStyles = createGlobalStyle`
  * {
    margin: 0;
    padding: 0;
    box-sizing: border-box;
    transition: background-color 0.3s, color 0.3s;
  }

  body {
    font-family: 'Poppins', sans-serif;
    line-height: 1.6;
    background-color: ${({ theme }) => theme.colors.background};
    color: ${({ theme }) => theme.colors.text};
  }

  a {
    text-decoration: none;
    color: inherit;
  }

  button {
    cursor: pointer;
  }

  .leaflet-container {
    height: 300px;
    width: 100%;
    border-radius: 8px;
  }
`;

// Star rating component
const Star = ({ filled }) => (
  <span style={{ color: filled ? '#f59e0b' : '#cbd5e1' }}>★</span>
);

// PatientName component
const PatientName = ({ children }) => (
  <p style={{ fontStyle: 'italic', margin: '0.5rem 0' }}>{children}</p>
);

// Styled components (all your styled components remain exactly the same as in your original code)
const AppContainer = styled.div`
  display: flex;
  flex-direction: column;
  min-height: 100vh;
`;

const Header = styled.header`
  padding: 1rem 2rem;
  display: flex;
  align-items: center;
  justify-content: space-between;
  background-color: ${({ theme }) => theme.colors.headerBg};
  box-shadow: ${({ theme }) => theme.shadows.small};
  position: sticky;
  top: 0;
  z-index: 1000;

  @media (max-width: 768px) {
    flex-direction: column;
    padding: 1rem;
    gap: 1rem;
  }
`;

const LogoContainer = styled.div`
  display: flex;
  align-items: center;
  gap: 10px;
`;

const LogoImg = styled.img`
  width: 50px;
  height: 50px;
  object-fit: contain;
`;

const LogoText = styled.span`
  font-size: 1.8rem;
  font-weight: 700;
  color: ${({ theme }) => theme.colors.primary};
  letter-spacing: -0.5px;
`;

const Nav = styled.nav`
  display: flex;
  gap: 1.5rem;

  @media (max-width: 768px) {
    gap: 1rem;
  }
`;

const NavLink = styled.a`
  text-decoration: none;
  color: ${({ theme }) => theme.colors.text};
  font-weight: 500;
  font-size: 1.1rem;
  transition: all 0.3s;
  position: relative;

  &:hover {
    color: ${({ theme }) => theme.colors.primary};
  }

  &::after {
    content: '';
    position: absolute;
    bottom: -5px;
    left: 0;
    width: 0;
    height: 2px;
    background: ${({ theme }) => theme.colors.primary};
    transition: width 0.3s;
  }

  &:hover::after {
    width: 100%;
  }

  @media (max-width: 768px) {
    font-size: 0.9rem;
  }
`;

const HeaderControls = styled.div`
  display: flex;
  align-items: center;
  gap: 1rem;
`;

const ThemeToggle = styled.button`
  background: none;
  border: none;
  color: ${({ theme }) => theme.colors.text};
  cursor: pointer;
  padding: 0.5rem;
  border-radius: 50%;
  display: flex;
  align-items: center;
  justify-content: center;
  transition: all 0.3s;

  &:hover {
    background-color: ${({ theme }) => theme.colors.primary}20;
    color: ${({ theme }) => theme.colors.primary};
  }
`;

const AuthButtons = styled.div`
  display: flex;
  gap: 1rem;

  @media (max-width: 768px) {
    margin-top: 0.5rem;
  }
`;

const Button = styled.button`
  padding: 0.6rem 1.2rem;
  border-radius: 8px;
  border: none;
  font-weight: 600;
  cursor: pointer;
  transition: all 0.3s;
  font-size: 1rem;
`;

const LoginButton = styled(Button)`
  background-color: transparent;
  color: ${({ theme }) => theme.colors.primary};
  border: 2px solid ${({ theme }) => theme.colors.primary};

  &:hover {
    background-color: ${({ theme }) => `${theme.colors.primary}10`};
  }
`;

const SignupButton = styled(Button)`
  background-color: ${({ theme }) => theme.colors.primary};
  color: white;

  &:hover {
    background-color: ${({ theme }) => `${theme.colors.primary}90`};
    transform: translateY(-2px);
    box-shadow: 0 4px 8px ${({ theme }) => `${theme.colors.primary}20`};
  }
`;

// Main Content Styles
const Main = styled.main`
  flex: 1;
`;

// Hero Section Styles
const HeroSection = styled.section`
  background: linear-gradient(135deg, #f0f9f8 0%, #e6f3f1 100%);
  padding: 4rem 2rem;
  display: flex;
  flex-wrap: wrap;
  align-items: center;
  justify-content: space-between;
  gap: 3rem;

  ${({ theme }) => theme === darkTheme && `
    background: linear-gradient(135deg, #1e3a8a 0%, #1e40af 100%);
  `}

  @media (max-width: 768px) {
    padding: 2rem 1rem;
    gap: 2rem;
  }
`;

const HeroContent = styled.div`
  flex: 1;
  min-width: 300px;
  max-width: 600px;
`;

const HeroTitle = styled.h1`
  font-size: 3rem;
  margin-bottom: 1.5rem;
  line-height: 1.2;
  font-weight: 700;
  color: ${({ theme }) => theme.colors.text};

  span {
    color: ${({ theme }) => theme.colors.primary};
  }

  @media (max-width: 768px) {
    font-size: 2.5rem;
  }

  @media (max-width: 480px) {
    font-size: 2rem;
  }
`;

const BlueText = styled.span`
  color: ${({ theme }) => theme.colors.secondary};
`;

const HeroDescription = styled.p`
  font-size: 1.1rem;
  color: ${({ theme }) => theme.colors.textLight};
  margin-bottom: 2rem;
  line-height: 1.7;
`;

const PrimaryButton = styled(Button)`
  background-color: ${({ theme }) => theme.colors.primary};
  color: white;
  padding: 0.8rem 1.8rem;
  font-size: 1.1rem;
  box-shadow: 0 4px 6px ${({ theme }) => `${theme.colors.primary}20`};

  &:hover {
    background-color: ${({ theme }) => theme.colors.primary}90;
    transform: translateY(-3px);
    box-shadow: 0 6px 12px ${({ theme }) => `${theme.colors.primary}30`};
  }
`;

const HeroImage = styled.img`
  flex: 1;
  min-width: 300px;
  max-width: 500px;
  width: 100%;
  height: auto;
  border-radius: 12px;
  box-shadow: ${({ theme }) => theme.shadows.large};
  transition: transform 0.3s;

  &:hover {
    transform: scale(1.02);
  }
`;

// Doctor Section Styles
const DoctorSection = styled.section`
  padding: 5rem 2rem;
  background-color: ${({ theme }) => theme.colors.background};

  @media (max-width: 768px) {
    padding: 3rem 1rem;
  }
`;

const DoctorContainer = styled.div`
  max-width: 1200px;
  margin: 0 auto;
  display: flex;
  flex-wrap: wrap;
  align-items: center;
  justify-content: center;
  gap: 3rem;

  @media (max-width: 768px) {
    gap: 2rem;
  }
`;

const DoctorImage = styled.img`
  flex: 1;
  min-width: 300px;
  max-width: 400px;
  border-radius: 12px;
  box-shadow: ${({ theme }) => theme.shadows.large};
`;

const DoctorDetails = styled.div`
  flex: 1;
  min-width: 300px;
  max-width: 600px;
`;

const DoctorName = styled.h1`
  font-size: 2.5rem;
  margin-bottom: 0.5rem;
  color: ${({ theme }) => theme.colors.text};

  @media (max-width: 480px) {
    font-size: 2rem;
  }
`;

const DoctorSpecialty = styled.h3`
  font-size: 1.3rem;
  color: ${({ theme }) => theme.colors.textLight};
  margin-bottom: 1.5rem;
  font-weight: 500;
`;

const DoctorQualification = styled.h5`
  font-size: 1rem;
  color: ${({ theme }) => theme.colors.textLight};
  margin-bottom: 0.5rem;
  display: flex;
  align-items: center;
  gap: 0.5rem;
`;

const DoctorExperience = styled.h5`
  font-size: 1rem;
  color: ${({ theme }) => theme.colors.textLight};
  margin-bottom: 1.5rem;
  display: flex;
  align-items: center;
  gap: 0.5rem;
`;

const DoctorBio = styled.p`
  margin: 1.5rem 0;
  font-size: 1.1rem;
  color: ${({ theme }) => theme.colors.textLight};
  line-height: 1.7;
`;

