    const [zoom, setZoom] = useState(map.getZoom());

    const handleZoomIn = () => {
      map.zoomIn();
      setZoom(map.getZoom());
    };

    const handleZoomOut = () => {
      map.zoomOut();
      setZoom(map.getZoom());
    };

    const handleZoomChange = (e) => {
      const newZoom = parseInt(e.target.value);
      map.setZoom(newZoom);
      setZoom(newZoom);
    };

    return (
      <MapZoomControl>
        <button onClick={handleZoomIn}>+</button>
        <RangeInput
          type="range"
          min="1"
          max="18"
          value={zoom}
          onChange={handleZoomChange}
        />
