          {/* Services Section */}
          <ServicesSection id="services">
            <SectionHeader>
              <SectionSubtitle>Our Specializations</SectionSubtitle>
              <SectionTitle>Services We Offer</SectionTitle>
              <SectionDescription>
                We provide comprehensive pain management and rehabilitation services for various conditions.
              </SectionDescription>
            </SectionHeader>
            <ServicesGrid>
              {servicesData.map((service) => (
                <ServiceCard key={service.id}>
                  <ServiceImage src={service.img} alt={service.title} />
                  <ServiceTitle>{service.title}</ServiceTitle>
                </ServiceCard>
              ))}
            </ServicesGrid>
            <SeeMoreButton>View All Services</SeeMoreButton>
          </ServicesSection>

          {/* Patient Resources Section */}
          <PatientResourcesSection id="resources">
            <ResourcesHeader>
              <ResourcesTitle>Patient Resources</ResourcesTitle>
              <ResourcesSubtitle>
                Helpful information and tools to support your treatment journey
              </ResourcesSubtitle>
            </ResourcesHeader>
            <ResourcesGrid>
              {resourcesData.map((resource) => {
                if (resource.type === 'testimonial') {
                  return (
                    <ResourceCard key={resource.id}>
                      <PatientImage src={resource.img} alt="Patient" />
                      <TestimonialQuote>
                        {resource.content}
                        <PatientName>{resource.author}</PatientName>
                      </TestimonialQuote>
                      <ResourceButton>{resource.buttonText}</ResourceButton>
                    </ResourceCard>
                  );
                } else if (resource.type === 'faq') {
                  return (
                    <ResourceCard key={resource.id}>
                      <ResourceTitleWithIcon>
                        {resource.title}
                      </ResourceTitleWithIcon>
                      <FaqList>
                        {resource.items.map((item, index) => (
                          <FaqItem key={index}>{item}</FaqItem>
                        ))}
                      </FaqList>
                      <ResourceButton>{resource.buttonText}</ResourceButton>
                    </ResourceCard>
                  );
                } else if (resource.type === 'calendar') {
                  return (
                    <ResourceCard key={resource.id}>
                      <IconWrapper>
                        <FaCalendarAlt size={32} />
                      </IconWrapper>
                      <ResourceTitle>{resource.title}</ResourceTitle>
                      <CalendarImage src={resource.img} alt="Calendar" />
                      <ResourceButton>Book Now</ResourceButton>
                    </ResourceCard>
                  );
                } else if (resource.type === 'blog') {
                  return (
                    <ResourceCard key={resource.id}>
                      <BlogImage src={resource.img} alt="Blog" />
                      <ResourceContent>
                        <ResourceCategory>Health & Wellness</ResourceCategory>
                        <ResourceTitle>{resource.title}</ResourceTitle>
                        <BlogExcerpt>{resource.content}</BlogExcerpt>
                        <ResourceMeta>
                          <ResourceDate>May 15, 2023</ResourceDate>
                          <ResourceAuthor>By Dr. Menon</ResourceAuthor>
                        </ResourceMeta>
                        <ResourceButton>{resource.buttonText}</ResourceButton>
                      </ResourceContent>
                    </ResourceCard>
                  );
                }
                return null;
              })}
            </ResourcesGrid>
          </PatientResourcesSection>

          {/* Contact Section */}
          <ContactSection id="contact">
            <SectionHeader>
              <SectionSubtitle>Get In Touch</SectionSubtitle>
              <SectionTitle>Contact Us</SectionTitle>
              <SectionDescription>
                Reach out to us for appointments, inquiries, or any questions you may have.
              </SectionDescription>
            </SectionHeader>

            <FormTabs>
              <TabButton 
                active={activeTab === 'contact'} 
                onClick={() => setActiveTab('contact')}
              >
                Contact Form
              </TabButton>
              <TabButton 
                active={activeTab === 'appointment'} 
                onClick={() => setActiveTab('appointment')}
              >
                Book Appointment
              </TabButton>
            </FormTabs>

            <FormContainer>
              {isSubmitted ? (
                <SuccessMessage>
                  <h3>Thank You!</h3>
                  <p>
                    {activeTab === 'contact' 
                      ? 'Your message has been sent successfully. We will get back to you soon.' 
                      : 'Your appointment request has been received. We will contact you to confirm the details.'}
                  </p>
                </SuccessMessage>
              ) : (
                <Form onSubmit={handleSubmit}>
                  <FormGroup>
                    <FormLabel>Full Name *</FormLabel>
                    <FormInput
                      type="text"
                      name="name"
                      value={formData.name}
                      onChange={handleInputChange}
                      error={formErrors.name}
                    />
                    {formErrors.name && <ErrorText>{formErrors.name}</ErrorText>}
                  </FormGroup>

                  <FormGroup>
                    <FormLabel>Email *</FormLabel>
                    <FormInput
                      type="email"
                      name="email"
                      value={formData.email}
                      onChange={handleInputChange}
                      error={formErrors.email}
                    />
                    {formErrors.email && <ErrorText>{formErrors.email}</ErrorText>}
                  </FormGroup>

                  <FormGroup>
                    <FormLabel>Phone Number</FormLabel>
                    <FormInput
                      type="tel"
                      name="phone"
                      value={formData.phone}
                      onChange={handleInputChange}
                    />
                  </FormGroup>

                  {activeTab === 'appointment' && (
                    <>
                      <FormGroup>
                        <FormLabel>Preferred Date *</FormLabel>
                        <FormInput
                          type="date"
                          name="date"
                          value={formData.date}
                          onChange={handleInputChange}
                          error={formErrors.date}
                        />
                        {formErrors.date && <ErrorText>{formErrors.date}</ErrorText>}
                      </FormGroup>

                      <FormGroup>
                        <FormLabel>Preferred Time *</FormLabel>
                        <FormInput
                          type="time"
                          name="time"
                          value={formData.time}
                          onChange={handleInputChange}
                          error={formErrors.time}
                        />
                        {formErrors.time && <ErrorText>{formErrors.time}</ErrorText>}
                      </FormGroup>

                      <FormGroup>
                        <FormLabel>Reason for Visit</FormLabel>
                        <FormInput
                          type="text"
                          name="reason"
                          value={formData.reason}
                          onChange={handleInputChange}
                          placeholder="Briefly describe your condition"
                        />
                      </FormGroup>
                    </>
                  )}

                  {activeTab === 'contact' && (
                    <FormGroup>
                      <FormLabel>Message</FormLabel>
                      <FormTextarea
                        name="message"
                        value={formData.message}
                        onChange={handleInputChange}
                        placeholder="How can we help you?"
                      />
                    </FormGroup>
                  )}

                  <PrimaryButton type="submit">
                    {activeTab === 'contact' ? 'Send Message' : 'Request Appointment'}
                  </PrimaryButton>
                </Form>
              )}
            </FormContainer>

            <MapWrapper>
              <SectionSubtitle>Our Location</SectionSubtitle>
              <MapContainer center={clinicLocation} zoom={13} scrollWheelZoom={false}>
                <TileLayer
                  url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
                  attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
                />
                <Marker position={clinicLocation}>
                  <Popup>
                    <strong>RelivaWell Clinic</strong><br />
                    123 Medical Plaza, Mumbai
                  </Popup>
                </Marker>
                <MapZoomController />
              </MapContainer>
              <MapAddress>
                <FaMapMarkerAlt /> 123 Medical Plaza, Mumbai, Maharashtra 400001
              </MapAddress>
              <MapHours>
                <FaClock /> Mon-Sat: 9:00 AM - 6:00 PM | Sun: Closed
              </MapHours>
            </MapWrapper>
          </ContactSection>
        </Main>

        {/* Footer */}
        <Footer>
          <FooterColumn>
            <LogoContainer>
              <LogoImg src={images.logo} alt="RelivaWell Logo" />
              <LogoText style={{ color: 'white' }}>Reliva<span>Well</span></LogoText>
            </LogoContainer>
            <FooterText>
              Providing expert pain management and rehabilitation services to help you live a pain-free life.
            </FooterText>
            <SocialIcons>
              <SocialIcon href="#"><i className="fab fa-facebook-f"></i></SocialIcon>
              <SocialIcon href="#"><i className="fab fa-twitter"></i></SocialIcon>
              <SocialIcon href="#"><i className="fab fa-instagram"></i></SocialIcon>
              <SocialIcon href="#"><i className="fab fa-linkedin-in"></i></SocialIcon>
            </SocialIcons>
          </FooterColumn>

          <FooterColumn>
            <FooterTitle>Quick Links</FooterTitle>
            <FooterLink href="#home">Home</FooterLink>
            <FooterLink href="#doctor">About Doctor</FooterLink>
            <FooterLink href="#services">Services</FooterLink>
            <FooterLink href="#resources">Patient Resources</FooterLink>
            <FooterLink href="#contact">Contact</FooterLink>
          </FooterColumn>

          <FooterColumn>
            <FooterTitle>Our Services</FooterTitle>
            <FooterLink href="#">Pain Management</FooterLink>
            <FooterLink href="#">Physical Therapy</FooterLink>
            <FooterLink href="#">Sports Rehabilitation</FooterLink>
            <FooterLink href="#">Post-Surgical Rehab</FooterLink>
            <FooterLink href="#">Chronic Pain Treatment</FooterLink>
          </FooterColumn>

          <FooterColumn>
            <FooterTitle>Newsletter</FooterTitle>
            <FooterText>
              Subscribe to our newsletter to receive updates and health tips.
            </FooterText>
            <NewsletterForm>
              <NewsletterInput type="email" placeholder="Your email address" />
              <NewsletterButton>Subscribe</NewsletterButton>
            </NewsletterForm>
          </FooterColumn>
        </Footer>

        <Copyright>
          &copy; {new Date().getFullYear()} RelivaWell. All Rights Reserved.
        </Copyright>
      </AppContainer>
    </ThemeProvider>
  );
};

export default App;
