import React, { useState, useEffect } from 'react';
import styled, { createGlobalStyle, ThemeProvider } from 'styled-components';
import { 
  FaMapMarkerAlt, 
  FaClock, 
  FaGraduationCap, 
  FaCalendarAlt, 
  FaSun, 
  FaMoon,
  FaPhone,
  FaEnvelope,
  FaFacebookF,
  FaTwitter,
  FaInstagram,
  FaLinkedinIn,
  FaYoutube,
  FaChevronDown,
  FaChevronUp,
  FaDownload,
  FaPlay
} from 'react-icons/fa';
import { MapContainer, TileLayer, Marker, Popup, useMap } from 'react-leaflet';
import 'leaflet/dist/leaflet.css';
import L from 'leaflet';
import { Carousel } from 'react-responsive-carousel';
import 'react-responsive-carousel/lib/styles/carousel.min.css';

// Import all images
import logo from './assets/logo.png';
import doctorImage from './assets/doctor.jpg';
import doctorProfile from './assets/medical.png';
import mapImage from './assets/map.jpg';
import calendarImage from './assets/may-calendar.png';
import stethoscopeImage from './assets/stethoscope.png';
import patient1 from './assets/patient1.jpg';
import patient2 from './assets/patient2.jpg';
import patient3 from './assets/patient3.jpg';
import backPain from './assets/back-pain.jpg';
import wristPain from './assets/wrist-joint-pain.jpg';
import slippedDisc from './assets/slipped-disc.jpg';
import neckPain from './assets/neck-pain.jpg';
import anklePain from './assets/ankle-joint-pain.png';
import headache from './assets/headache.jpg';
import kneePain from './assets/knee-pain.jpg';
import sportInjuries from './assets/sport-injuries.jpg';
import clinicBuilding from './assets/clinic-building.jpg';
import treatmentRoom from './assets/treatment-room.jpg';
import videoThumbnail from './assets/video-thumbnail.jpg';
import healthTips from './assets/health-tips.jpg';
import exerciseGuide from './assets/exercise-guide.jpg';

// Fix for leaflet marker icons
delete L.Icon.Default.prototype._getIconUrl;
L.Icon.Default.mergeOptions({
  iconRetinaUrl: 'https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.7.1/images/marker-icon-2x.png',
  iconUrl: 'https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.7.1/images/marker-icon.png',
  shadowUrl: 'https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.7.1/images/marker-shadow.png',
});

// Image object
const images = {
  logo,
  logoWhite,
  doctorImage,
  doctorProfile,
  mapImage,
  calendarImage,
  stethoscopeImage,
  patient1,
  patient2,
  patient3,
  backPain,
  wristPain,
  slippedDisc,
  neckPain,
  anklePain,
  headache,
  kneePain,
  sportInjuries,
  clinicBuilding,
  treatmentRoom,
  videoThumbnail,
  healthTips,
  exerciseGuide
};

// Theme definitions
const lightTheme = {
  colors: {
    primary: '#059669',
    secondary: '#3B82F6',
    background: '#ffffff',
    text: '#333333',
    textLight: '#666666',
    headerBg: '#ffffff',
    cardBg: '#ffffff',
    footerBg: '#0f172a',
    footerText: '#cbd5e1',
    primaryLight: '#d1fae5',
    primaryDark: '#065f46',
    backgroundDark: '#f3f4f6',
  },
  shadows: {
    small: '0 2px 15px rgba(0, 0, 0, 0.1)',
    medium: '0 5px 15px rgba(0, 0, 0, 0.1)',
    large: '0 10px 25px rgba(0, 0, 0, 0.1)',
  },
};

const darkTheme = {
  colors: {
    primary: '#10b981',
    secondary: '#60a5fa',
    background: '#1e293b',
    text: '#f8fafc',
    textLight: '#94a3b8',
    headerBg: '#1e293b',
    cardBg: '#334155',
    footerBg: '#0f172a',
    footerText: '#cbd5e1',
    primaryLight: '#064e3b',
    primaryDark: '#a7f3d0',
    backgroundDark: '#0f172a',
  },
  shadows: {
    small: '0 2px 15px rgba(0, 0, 0, 0.3)',
    medium: '0 5px 15px rgba(0, 0, 0, 0.3)',
    large: '0 10px 25px rgba(0, 0, 0, 0.3)',
  },
};

// Global styles
const GlobalStyles = createGlobalStyle`
  * {
    margin: 0;
    padding: 0;
    box-sizing: border-box;
    transition: background-color 0.3s, color 0.3s;
  }

  body {
    font-family: 'Poppins', sans-serif;
    line-height: 1.6;
    background-color: ${({ theme }) => theme.colors.background};
    color: ${({ theme }) => theme.colors.text};
    overflow-x: hidden;
  }

  a {
    text-decoration: none;
    color: inherit;
  }

  button {
    cursor: pointer;
    font-family: 'Poppins', sans-serif;
  }

  .leaflet-container {
    height: 300px;
    width: 100%;
    border-radius: 8px;
  }

  /* Custom scrollbar */
  ::-webkit-scrollbar {
    width: 8px;
  }

  ::-webkit-scrollbar-track {
    background: ${({ theme }) => theme.colors.backgroundDark};
  }

  ::-webkit-scrollbar-thumb {
    background: ${({ theme }) => theme.colors.primary};
    border-radius: 4px;
  }

  /* Animation classes */
  @keyframes fadeIn {
    from { opacity: 0; }
    to { opacity: 1; }
  }

  @keyframes slideUp {
    from { 
      opacity: 0;
      transform: translateY(20px);
    }
    to { 
      opacity: 1;
      transform: translateY(0);
    }
  }

  .fade-in {
    animation: fadeIn 0.5s ease-in-out;
  }

  .slide-up {
    animation: slideUp 0.5s ease-out;
  }

  /* Responsive table */
  .responsive-table {
    width: 100%;
    border-collapse: collapse;
    margin: 1rem 0;

    th, td {
      padding: 0.75rem;
      text-align: left;
      border-bottom: 1px solid ${({ theme }) => theme.colors.textLight}20;
    }

    th {
      background-color: ${({ theme }) => theme.colors.primary}10;
      color: ${({ theme }) => theme.colors.primary};
      font-weight: 600;
    }

    tr:hover {
      background-color: ${({ theme }) => theme.colors.primary}05;
    }

    @media (max-width: 768px) {
      display: block;
      overflow-x: auto;
      white-space: nowrap;
    }
  }
`;

// Star rating component
const Star = ({ filled }) => (
  <span style={{ color: filled ? '#f59e0b' : '#cbd5e1' }}>★</span>
);

// PatientName component
const PatientName = ({ children }) => (
  <p style={{ fontStyle: 'italic', margin: '0.5rem 0' }}>{children}</p>
);

// Loading spinner component
const LoadingSpinner = () => (
  <div style={{ 
    display: 'flex', 
    justifyContent: 'center', 
    alignItems: 'center', 
    height: '100px',
    color: '#059669'
  }}>
    <div className="spinner"></div>
  </div>
);

// Styled components
const AppContainer = styled.div`
  display: flex;
  flex-direction: column;
  min-height: 100vh;
`;

const Header = styled.header`
  padding: 1rem 2rem;
  display: flex;
  align-items: center;
  justify-content: space-between;
  background-color: ${({ theme }) => theme.colors.headerBg};
  box-shadow: ${({ theme }) => theme.shadows.small};
  position: sticky;
  top: 0;
  z-index: 1000;
  transition: all 0.3s;

  @media (max-width: 1024px) {
    padding: 1rem;
  }

  @media (max-width: 768px) {
    flex-direction: column;
    gap: 1rem;
    padding: 1rem;
    position: relative;
  }
`;

const LogoContainer = styled.div`
  display: flex;
  align-items: center;
  gap: 10px;
`;

const LogoImg = styled.img`
  width: 50px;
  height: 50px;
  object-fit: contain;
  transition: all 0.3s;

  @media (max-width: 768px) {
    width: 40px;
    height: 40px;
  }
`;

const LogoText = styled.span`
  font-size: 1.8rem;
  font-weight: 700;
  color: ${({ theme }) => theme.colors.primary};
  letter-spacing: -0.5px;
  transition: all 0.3s;

  @media (max-width: 768px) {
    font-size: 1.5rem;
  }
`;

const Nav = styled.nav`
  display: flex;
  gap: 1.5rem;
  align-items: center;

  @media (max-width: 1024px) {
    gap: 1rem;
  }

  @media (max-width: 768px) {
    width: 100%;
    flex-direction: column;
    gap: 0.5rem;
    display: ${({ mobileMenuOpen }) => mobileMenuOpen ? 'flex' : 'none'};
    padding: 1rem 0;
  }
`;

const NavLink = styled.a`
  text-decoration: none;
  color: ${({ theme }) => theme.colors.text};
  font-weight: 500;
  font-size: 1.1rem;
  transition: all 0.3s;
  position: relative;
  padding: 0.5rem 0;

  &:hover {
